import React, { useEffect } from "react";
import { useForm } from "react-hook-form";

import { makeStyles } from "@material-ui/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";

import Footer from "../components/footer";
import GetWindow from "../components/getWindow";

import background from "../assets/bgContact.png";

const useStyles = makeStyles((theme) => ({
  articleContainer: {
    marginTop: "10vh",
    justifyContent: "center",
    alignItems: "center",
    verticalAlign: "center",
  },
  servicesBox: {
    marginTop: "5em",
    marginRight: "2em",
    marginLeft: "2em",
    marginBottom: "2em",
    justifyContent: "center",
    alignItems: "center",
    display: "flex",
    flexWrap: "wrap",
  },
  typeTitle: {
    color: theme.palette.common.gray,
    fontSize: "2em",
    fontFamily: "Lato",
    fontWeight: 500,
  },
  typeSubTitle: {
    color: theme.palette.common.gray,
    fontSize: "1.5em",
    fontFamily: "Lato",
    fontWeight: 500,
  },
  type: {
    color: theme.palette.common.gray,
    fontSize: "1em",
    fontFamily: "Lato",
    fontWeight: 0,
    marginLeft: "2em",
    marginRight: "2em",
  },
  divLine: {
    width: "65%",
    color: theme.palette.common.blue,
    backgroundColor: theme.palette.common.blue,
    height: 1,
  },
}));

const Contact = () => {
  const classes = useStyles();
  const { width } = GetWindow();
  let articleWidth = width > 800 ? "75%" : "100%";

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm();

  const onSubmit = async (data) => {
    const { name, email, subject, message } = data;

    console.log("Name: ", name);
    console.log("Email: ", email);
    console.log("Subject: ", subject);
    console.log("Message: ", message);
  };

  useEffect(() => {
    document.body.style = `background-image: url("${background}")`;
  }, []);

  return (
    <Grid
      container
      spacing={2}
      alignItems="stretch"
      justifyContent="center"
      style={{ height: "100%" }}
    >
      <Paper
        elevation={0}
        className={classes.articleContainer}
        style={{ width: articleWidth }}
      >
        <Box className={classes.servicesBox}>
          <Typography className={classes.typeTitle} component="p">
            Mobile Data Centers
          </Typography>
          <br />
        </Box>
        <Box
          style={{
            justifyContent: "center",
            alignItems: "center",
            display: "flex",
            flexWrap: "wrap",
          }}
        >
          <hr className={classes.divLine} style={{ float: "left" }} />
        </Box>
      </Paper>
    </Grid>
  );
};

export default Contact;
