import React, { useEffect } from 'react';

import { makeStyles } from '@material-ui/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';

import Footer from '../components/footer';
import GetWindow from '../components/getWindow';

import background from '../assets/bgServices.png';

const useStyles = makeStyles(theme => ({
    articleContainer: {
        marginTop: "8vh",
        width: '75%',
        justifyContent: "center",
        alignItems: "center",
        verticalAlign: "center",
    },
    servicesBox: {
        marginTop: "5em",
        marginRight: "2em",
        marginLeft: "2em",
        marginBottom: "3em",
        justifyContent: "center",
        alignItems: "center",
        display: "flex",
        flexWrap: "wrap",
    },
    typeTitle: {
        color: theme.palette.common.gray,
        fontSize: "3em",
        fontFamily: "Lato",
        fontWeight: 500,
    },
    type: {
        color: theme.palette.common.gray,
        fontSize: "1em",
        fontFamily: "Lato",
        fontWeight: 0,
    },
    divLine: {
      width: "100%",
      color: theme.palette.common.blue,
      backgroundColor: theme.palette.common.blue,
      height: 1
    }
}));

const Services = () => {
    const {height, width} = GetWindow();
    const classes = useStyles();

    useEffect(() => {
        document.body.style = `background-image: url("${background}")`;
      }, []);

    return (
        <>
            <Grid container spacing={2} alignItems="stretch" justifyContent="center" style={{height: "100%"}}>
                <Paper elevation={0} className={classes.articleContainer}>
                    <Box className={classes.servicesBox}>
                        <hr className={classes.divLine} style={{float: "left"}} />
                        <Grid item>
                            <Typography className={classes.typeTitle} component="p" >Services We Offer</Typography>
                        </Grid>
                        <hr className={classes.divLine} style={{float: "left"}} />
                    </Box>
                    <Box className={classes.servicesBox}>
                        <Grid item xs={5} style={{textAlign: "center"}}>
                            <Button target="_blank" href="https://www.vertiv.com/en-us/products/brands/liebert/" >
                                <img alt="liebert" src = {liebert} className = {classes.logo}/>
                            </Button>
                        </Grid>
                        <Grid item xs={5}>
                            <Typography className={classes.brandName} component="p" >Liebert is a global leader in critical continuity products and services. In this rapidly-changing business, Liebert provides solutions to ensure the IT department is protected and always running so that your business operates continuously and seamlessly, with minimal or no downtime.</Typography>
                        </Grid>
                    </Box>
                </Paper>
                <Footer />
            </Grid>
        </>
    );
};

export default Services;
