import React, { useEffect } from 'react';

import { makeStyles } from '@material-ui/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';

import Footer from '../components/footer';
import GetWindow from '../components/getWindow';

import background from '../assets/bgAbout.png';

const useStyles = makeStyles(theme => ({
    articleContainer: {
        marginTop: "8vh",
        width: '75%',
        justifyContent: "center",
        alignItems: "center",
        verticalAlign: "center",
    },
    aboutUsBox: {
        marginTop: "3em",
        marginRight: "2em",
        marginLeft: "2em",
        marginBottom: "3em",
        justifyContent: "center",
        alignItems: "center",
        display: "flex",
        flexWrap: "wrap",
    },
    type: {
        color: theme.palette.common.gray,
        fontSize: "1em",
        fontFamily: "Lato",
        fontWeight: 0,
    },
}));

const AboutUs = () => {
    const {height, width} = GetWindow();
    const classes = useStyles();

    useEffect(() => {
        document.body.style = `background-image: url("${background}")`;
      }, []);

    return (
        <>
            <Grid container spacing={2} alignItems="stretch" justifyContent="center" style={{height: "100%"}}>
                <Paper elevation={0} className={classes.articleContainer}>
                    <Box className={classes.aboutUsBox}>
                        <Grid>
                            <Typography className={classes.type} component="p" >AC Engineering has over 28 years of continuous sales and design experience in the critical application and solutions for data centers. ACE is committed to provide integrated solutions for mission critical applications ranging from the smallest server room to the largest data center. In addition, ACE is able to support its customers during and after the construction process, including all aspects of system procurement, start-up, commissioning, monitoring and maintenance to ensure that these systems work as designed, and adapt to the companyâ€™s needs. Some of the products represented by AC Engineering are:</Typography>
                        </Grid>
                    </Box>
                </Paper>
            </Grid>
        </>
    );
};

export default AboutUs;
