import React, { useEffect } from "react";
import { useForm } from "react-hook-form";

import { makeStyles } from "@material-ui/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";

import Footer from "../components/footer";
import GetWindow from "../components/getWindow";

import background from "../assets/bgContact.png";

const useStyles = makeStyles((theme) => ({
  articleContainer: {
    marginTop: "10vh",
    justifyContent: "center",
    alignItems: "center",
    verticalAlign: "center",
  },
  servicesBox: {
    marginTop: "2em",
    marginRight: "2em",
    marginLeft: "2em",
    justifyContent: "center",
    alignItems: "center",
    display: "flex",
    flexWrap: "wrap",
  },
  typeTitle: {
    color: theme.palette.common.gray,
    fontSize: "2em",
    fontFamily: "Lato",
    fontWeight: 500,
  },
  type: {
    color: theme.palette.common.gray,
    fontSize: "1em",
    fontFamily: "Lato",
    fontWeight: 0,
    marginLeft: "2em",
    marginRight: "2em",
  },
  divLine: {
    width: "65%",
    color: theme.palette.common.blue,
    backgroundColor: theme.palette.common.blue,
    height: 1,
  },
  inputTitle: {
    fontWeight: "bold",
    fontFamily: "Lato",
    marginTop: "1em",
  },
  formInput: {
    width: "75%",
    height: "2em",
  },
  message: {
    width: "75%",
    height: "8em",
  },
  errorMessage: {
    color: "red",
  },
  submitButt: {
    margin: "auto",
    fontFamily: "Lato",
    marginTop: "1em",
  },
}));

const Contact = () => {
  const classes = useStyles();
  const { width } = GetWindow();
  let articleWidth = width > 800 ? "75%" : "100%";

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm();

  const onSubmit = async (data) => {
    const { name, email, subject, message } = data;

    console.log("Name: ", name);
    console.log("Email: ", email);
    console.log("Subject: ", subject);
    console.log("Message: ", message);
  };

  useEffect(() => {
    document.body.style = `background-image: url("${background}")`;
  }, []);

  return (
    <Grid
      container
      spacing={2}
      alignItems="stretch"
      justifyContent="center"
      style={{ height: "100%" }}
    >
      <Paper
        elevation={0}
        className={classes.articleContainer}
        style={{ width: articleWidth }}
      >
        <Box className={classes.servicesBox} style={{ marginTop: "5em" }}>
          <Typography className={classes.typeTitle} component="p">
            Contact Us
          </Typography>
          <br />
        </Box>
        <Box
          style={{
            justifyContent: "center",
            alignItems: "center",
            display: "flex",
            flexWrap: "wrap",
          }}
        >
          <hr className={classes.divLine} style={{ float: "left" }} />
        </Box>
        <Box className={classes.servicesBox}>
          <Typography className={classes.type} component="p">
            Thank you for your interest in AC Engineering, Inc. For more
            information, please contact us directly by completing the form or by
            calling us at the telephone and/or address provided below:
          </Typography>
        </Box>
        <Box className={classes.servicesBox}></Box>
        <Box
          className={classes.servicesBox}
          style={{ textAlign: "center", alignItems: "center" }}
          display="flex"
          flexDirection={width > 850 ? "row" : "column"}
        >
          <Grid item xs={5} style={{ textAlign: "center", margin: "auto" }}>
            <Typography className={classes.type} component="p">
              Joe E. Diaz, PE
            </Typography>
            <Typography
              className={classes.type}
              style={{ textDecoration: "none" }}
              component="a"
              href="mailto:jdiaz@acengineering.com"
            >
              jdiaz@acengineering.com
            </Typography>
            <br />
            <br />
            <Typography className={classes.type} component="p">
              J.R. Diaz
            </Typography>
            <Typography
              className={classes.type}
              style={{ textDecoration: "none" }}
              component="a"
              href="mailto:jrdiaz@acengineering.com"
            >
              jrdiaz@acengineering.com
            </Typography>
            <br />
            <br />
            <Typography
              className={classes.type}
              style={{ fontWeight: "bold" }}
              component="p"
            >
              AC Engineering, Inc.
            </Typography>
            <Typography className={classes.type} component="p">
              14024 SW 140th St.
            </Typography>
            <Typography className={classes.type} component="p">
              Miami, FL 33186
            </Typography>
            <br />
            <Typography
              className={classes.type}
              style={{ textDecoration: "none" }}
              component="a"
              href="tel:305.594.6996"
            >
              T: 305.594.6996
            </Typography>
            <Typography className={classes.type} component="p">
              F: 305.594.7678
            </Typography>
          </Grid>
          <Grid item xs={5} style={{ textAlign: "left", margin: "auto" }}>
            <div className="contactForm">
              <form
                id="contact-form"
                onSubmit={handleSubmit(onSubmit)}
                noValidate
              >
                {/* Row 1 of form */}
                <div className="row formRow">
                  <div className="col-6">
                    <Typography className={classes.inputTitle} component="p">
                      Name*
                    </Typography>
                    <input
                      type="text"
                      name="name"
                      {...register("name", {
                        required: {
                          value: true,
                          message: "Please enter your name",
                        },
                        maxLength: {
                          value: 30,
                          message: "Please use 30 characters or less",
                        },
                      })}
                      className={classes.formInput}
                      placeholder="Name"
                    ></input>
                    <br />
                    {errors.name && (
                      <span className={classes.errorMessage}>
                        {errors.name.message}
                      </span>
                    )}
                  </div>
                  <div className="col-6">
                    <Typography className={classes.inputTitle} component="p">
                      Email*
                    </Typography>
                    <input
                      type="email"
                      name="email"
                      {...register("email", {
                        required: true,
                        pattern:
                          /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/,
                      })}
                      className={classes.formInput}
                      placeholder="Email address"
                    ></input>
                    <br />
                    {errors.email && (
                      <span className={classes.errorMessage}>
                        Please enter a valid email address
                      </span>
                    )}
                  </div>
                </div>
                {/* Row 2 of form */}
                <div className="row formRow">
                  <div className="col">
                    <Typography className={classes.inputTitle} component="p">
                      Subject*
                    </Typography>
                    <input
                      type="text"
                      name="subject"
                      {...register("subject", {
                        required: {
                          value: true,
                          message: "Please enter a subject",
                        },
                        maxLength: {
                          value: 75,
                          message: "Subject cannot exceed 75 characters",
                        },
                      })}
                      className={classes.formInput}
                      placeholder="Subject"
                    ></input>
                    <br />
                    {errors.subject && (
                      <span className={classes.errorMessage}>
                        {errors.subject.message}
                      </span>
                    )}
                  </div>
                </div>
                {/* Row 3 of form */}
                <div className="row formRow">
                  <div className="col">
                    <Typography className={classes.inputTitle} component="p">
                      Message*
                    </Typography>
                    <textarea
                      rows={3}
                      name="message"
                      {...register("message", {
                        required: true,
                      })}
                      className={classes.message}
                      placeholder="Message"
                    ></textarea>
                    <br />
                    {errors.message && (
                      <span className={classes.errorMessage}>
                        Please enter a message
                      </span>
                    )}
                  </div>
                </div>
                <Button
                  className={classes.submitButt}
                  type="submit"
                  variant="outlined"
                >
                  Submit
                </Button>
              </form>
            </div>
          </Grid>
        </Box>
        <Box className={classes.servicesBox}></Box>
      </Paper>
      <Footer />
    </Grid>
  );
};

export default Contact;
