import React, { useState, useEffect } from "react";
import AppBar from "@material-ui/core/AppBar";
import useScrollTrigger from "@material-ui/core/useScrollTrigger";
import { makeStyles } from "@material-ui/styles";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Box from "@material-ui/core/Box";
import Grid from "@material-ui/core/Grid";
import { IconButton, Drawer, Hidden, Toolbar } from "@material-ui/core";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import { Link } from "react-router-dom";

import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";

import { FaBars } from "react-icons/fa";
import { IoIosCloseCircleOutline } from "react-icons/io";

import GetWindow from "./getWindow";

import logo from "../assets/logo.png";

function ElevationScroll(props) {
  const { children } = props;

  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0,
  });

  return React.cloneElement(children, {
    elevation: trigger ? 4 : 0,
  });
}

const useStyles = makeStyles((theme) => ({
  toolbarMargin: {
    ...theme.mixins.toolbar,
    marginBottom: "2em",
  },
  logo: {
    height: "6em",
  },
  logoContainer: {
    marginLeft: "4em",
    marginTop: "1.5em",
    marginBottom: "1em",
    padding: 0,
    "&:hover": {
      backgroundColor: "transparent",
    },
  },
  tabContainer: {
    marginLeft: "auto",
    marginRight: "4em",
  },
  tab: {
    ...theme.typography.tab,
    color: theme.palette.common.gray,
    minWidth: 10,
    marginLeft: "25px",
  },
  menu: {
    backgroundColor: theme.palette.common.blue,
    color: "white",
    borderRadius: "0px",
  },
  menuItem: {
    ...theme.typography.tab,
    opacity: 0.7,
    "&:hover": {
      opacity: 1,
    },
  },
  serve: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    margin: "1em",
    fontFamily: "Lato",
    textTransform: "none",
    fontSize: "1.5em",
    color: theme.palette.common.gray,
  },
  menuOptions: {
    textAlign: "center",
  },
  menuText: {
    fontFamily: "Lato",
    color: theme.palette.common.gray,
  },
  icons: {
    height: "2.5rem",
    width: "2.5rem",
  },
  exit: {
    height: "2rem",
    width: "2rem",
  },
  divLine: {
    width: "75%",
    color: theme.palette.common.gray,
    backgroundColor: theme.palette.common.gray,
    height: 1,
    display: "flex",
    float: "center",
  },
}));

const Header = ({ container }) => {
  const classes = useStyles();
  const [value, setValue] = useState(0);
  const { height, width } = GetWindow();
  const [displayMenu, setDisplay] = useState(false);
  let onMobile = width > 1300 ? false : true;

  const handleDrawerToggle = () => {
    setDisplay(!displayMenu);
  };

  const handleChange = (e, value) => {
    setValue(value);
  };

  useEffect(() => {
    switch (window.location.pathname) {
      case "/":
        if (value !== 0) {
          setValue(0);
        }
        break;
      case "/about":
        if (value !== 1) {
          setValue(1);
        }
        break;
      case "/services":
        if (value !== 2) {
          setValue(2);
        }
        break;
      case "/mobiledatacenters":
        if (value !== 3) {
          setValue(3);
        }
        break;
      case "/contact":
        if (value !== 4) {
          setValue(4);
        }
        break;
      default:
        break;
    }
  }, [value]);

  return (
    <React.Fragment>
      <ElevationScroll>
        <AppBar position="fixed" color="secondary">
          <Grid
            justify="space-between" // Add it here :)
            container
            spacing={24}
          >
            <Toolbar disableGutters>
              <Button
                component={Link}
                to="/"
                disableRipple
                onClick={() => setValue(0)}
                className={classes.logoContainer}
              >
                <img alt="company logo" src={logo} className={classes.logo} />
              </Button>
              {!onMobile && (
                <Tabs
                  value={value}
                  onChange={handleChange}
                  indicatorColor="primary"
                  className={classes.tabContainer}
                >
                  <Tab
                    className={classes.tab}
                    component={Link}
                    to="/"
                    label="Home"
                  />
                  <Tab
                    className={classes.tab}
                    component={Link}
                    to="/about"
                    label="About Us"
                  />
                  <Tab
                    className={classes.tab}
                    component={Link}
                    to="/services"
                    label="Services"
                  />
                  <Tab
                    className={classes.tab}
                    component={Link}
                    to="/mobiledatacenters"
                    label="Mobile Data Centers"
                  />
                  <Tab
                    className={classes.tab}
                    component={Link}
                    to="/contact"
                    label="Contact Us"
                  />
                </Tabs>
              )}
              {onMobile && (
                <IconButton
                  onClick={() => {
                    setDisplay(!displayMenu);
                  }}
                  style={{
                    float: "right",
                  }}
                >
                  <FaBars className={classes.icons} />
                </IconButton>
              )}
              {onMobile && (
                <nav aria-label="mailbox folders">
                  <Hidden xsDown implementation="css">
                    <Drawer
                      container={container}
                      variant="temporary"
                      anchor={"right"}
                      open={displayMenu}
                      onClose={handleDrawerToggle}
                      ModalProps={{
                        keepMounted: true,
                      }}
                    >
                      <Box className={classes.menuPopout}>
                        <Box m={3}>
                          <Grid container spacing={2} justifyContent="flex-end">
                            <Grid item>
                              <IconButton
                                onClick={() => setDisplay(!displayMenu)}
                              >
                                <IoIosCloseCircleOutline
                                  className={classes.exit}
                                />
                              </IconButton>
                            </Grid>
                          </Grid>
                        </Box>
                        <List>
                          <Link to={"/"} style={{ textDecoration: "none" }}>
                            <ListItem
                              button
                              key="home"
                              onClick={() => setDisplay(!displayMenu)}
                              className={classes.menuOptions}
                            >
                              <ListItemText
                                className={classes.menuText}
                                primary={"Home"}
                              />
                            </ListItem>
                          </Link>
                          <hr className={classes.divLine} />
                          <Link
                            to={"/about"}
                            style={{ textDecoration: "none" }}
                          >
                            <ListItem
                              button
                              key="about"
                              onClick={() => setDisplay(!displayMenu)}
                              className={classes.menuOptions}
                            >
                              <ListItemText
                                primary={"About Us"}
                                className={classes.menuText}
                              />
                            </ListItem>
                          </Link>
                          <hr className={classes.divLine} />
                          <Link
                            to={"/services"}
                            style={{ textDecoration: "none" }}
                          >
                            <ListItem
                              button
                              key="services"
                              onClick={() => setDisplay(!displayMenu)}
                              className={classes.menuOptions}
                            >
                              <ListItemText
                                primary={"Services"}
                                className={classes.menuText}
                              />
                            </ListItem>
                          </Link>
                          <hr className={classes.divLine} />
                          <Link
