import React, { useState, useEffect } from "react";

import { makeStyles } from "@material-ui/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";

import Footer from "../components/footer";
import GetWindow from "../components/getWindow";

import { getClients } from "../actions/clientsActions";
import LinearProgress from "@material-ui/core/LinearProgress";

import background from "../assets/bgMDC.png";

const useStyles = makeStyles((theme) => ({
  articleContainer: {
    marginTop: "8vh",
    justifyContent: "center",
    alignItems: "center",
    verticalAlign: "center",
  },
  servicesBox: {
    marginTop: "5em",
    marginRight: "2em",
    marginLeft: "2em",
    marginBottom: "2em",
    justifyContent: "center",
    alignItems: "center",
    display: "flex",
    flexWrap: "wrap",
  },
  typeTitle: {
    color: theme.palette.common.gray,
    fontSize: "2em",
    fontFamily: "Lato",
    fontWeight: 500,
  },
  typeSubTitle: {
    color: theme.palette.common.gray,
    fontSize: "1.5em",
    fontFamily: "Lato",
    fontWeight: 500,
  },
  type: {
    color: theme.palette.common.gray,
    fontSize: "1em",
    fontFamily: "Lato",
    fontWeight: 0,
    marginLeft: "2em",
    marginRight: "2em",
  },
  divLine: {
    width: "65%",
    color: theme.palette.common.blue,
    backgroundColor: theme.palette.common.blue,
    height: 1,
  },
}));

// const Client = (client) => {
//   return (
//     <>
//       <Grid
//         item
//         xs={3}
//         xl={6}
//         style={{ textAlign: "center", marginTop: "1em", marginBottom: "1em" }}
//       >
//         <Box display="flex" flexDirection="column" pb={2}>
//           <img
//             alt=""
//             src={client.recipe.picture.url}
//             height={height}
//             width={width}
//           />
//           <a
//             href={recipe.recipe.link}
//             target="_blank"
//             rel="noreferrer"
//             className={classes.link}
//           >
//             <b>{recipe.recipe.title}</b>
//           </a>
//         </Box>
//       </Grid>
//     </>
//   );
// };

const Clients = () => {
  const classes = useStyles();
  const { width } = GetWindow();
  let articleWidth = width > 800 ? "75%" : "100%";

  const [showSpinner, setShowSpinner] = useState(false);
  const [clients, setClients] = useState([]);

  useEffect(() => {
    document.body.style = `background-image: url("${background}")`;
  }, []);

  useEffect(() => {
    const refreshClients = async () => {
      try {
        setShowSpinner(true);
        let response = await getClients();

        setClients(response);
        setShowSpinner(false);
      } catch (error) {
        console.error(error);
        setShowSpinner(false);
      }
    };
    if (clients === undefined || clients.length === 0) refreshClients();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <>
      <Grid container spacing={2} alignItems="stretch" justifyContent="center">
        <Paper
          elevation={0}
          className={classes.articleContainer}
          style={{ width: articleWidth }}
        >
          <Box className={classes.servicesBox}>
            <Typography className={classes.typeTitle} component="p">
              Clients
            </Typography>
            <br />
          </Box>

          <Box
            style={{
              justifyContent: "center",
              alignItems: "center",
              display: "flex",
              flexWrap: "wrap",
            }}
          >
            <hr className={classes.divLine} style={{ float: "left" }} />
          </Box>

          {showSpinner && <LinearProgress />}

          <Box
            style={{
              justifyContent: "center",
              alignItems: "center",
              display: "flex",
              flexWrap: "wrap",
            }}
          >
            {/* {clients !== undefined &&
              clients.length !== 0 &&
              clients.map((client, index) => {
                return <Client key={index} client={client} />;
              })} */}
          </Box>
          <Box className={classes.servicesBox}></Box>
          <Box className={classes.servicesBox}></Box>
        </Paper>
        <Footer />
      </Grid>
    </>
  );
};

export default Clients;
